<div id="skills" class="animated fadeIn w-full mx-auto pb-12 px-5">
    <div x-data="{ page: 'pageOne' }" class="items-center transition-all duration-300 ease-initial">
        <div class="p-5 flex justify-center md:justify-start">
            <strong class="text-3xl hover:text-yellow-500 md:&mdash;&mdash;">Skills</strong>
        </div>

        {{-- Navigasii --}}
        <div class="navigation pb-6 flex justify-center md:justify-start">
            <button @click="page = 'pageOne'"
                :class="{ ' text-yellow-500': page === 'pageOne', 'bg-dark-200 text-white': page !== 'pageOne' }"
                class="px-4 py-2 bg-dark text-white text-md  rounded hover:bg-yellow-600">Tech Stack</button>
            <button @click="page = 'pageTwo'"
                :class="{ ' text-yellow-500': page === 'pageTwo', 'bg-dark-200 text-white': page !== 'pageTwo' }"
                class="px-4 py-2 bg-dark  text-white text-md  rounded hover:bg-yellow-600">Tools</button>
            <button @click="page = 'pageThree'"
                :class="{ ' text-yellow-500': page === 'pageThree', 'bg-dark-200 text-white': page !== 'pageThree' }"
                class="px-4 py-2 bg-dark  text-white text-md  rounded hover:bg-yellow-600">Certificate</button>
        </div>

